// Extract tenant_id from the failed workflow's execution data
const executionData = $input.first().json.execution?.data?.resultData?.runData || {};

let tenant_id = null;
let remote_jid = null;
let instance_name = null;

// Search known node outputs for tenant_id
const searchPaths = [
  'Load Tenant Config',
  'Parse Webhook Data',
  'Merge Tenant Config',
  'Lookup Tenant by Chat ID',
  'Load All Active Professionals',
  'Query Tenant Config',
  'Enrich with Tenant Config'
];

for (const nodeName of searchPaths) {
  if (tenant_id) break;
  const nodeData = executionData[nodeName];
  if (!nodeData?.[0]?.data?.main?.[0]) continue;

  for (const item of nodeData[0].data.main[0]) {
    const json = item.json || {};
    // Direct tenant_id field
    if (json.tenant_id) { tenant_id = json.tenant_id; break; }
    // Nested in tenant_config object
    if (json.tenant_config?.tenant_id) { tenant_id = json.tenant_config.tenant_id; break; }
  }
}

// Extract remote_jid for patient fallback
const parseNode = executionData['Parse Webhook Data'];
if (parseNode?.[0]?.data?.main?.[0]?.[0]?.json) {
  const parsed = parseNode[0].data.main[0][0].json;
  remote_jid = parsed.remote_jid || null;
  instance_name = parsed.instance_name || null;
}

return {
  tenant_id,
  remote_jid,
  instance_name,
  workflow: $input.first().json.workflow,
  execution: {
    id: $input.first().json.execution?.id,
    url: $input.first().json.execution?.url
  },
  error: $input.first().json.error
};

// Merge tenant config with extracted data
const tenantConfig = $input.first().json;
const extracted = $('Extract Tenant ID').first().json;

return {
  // Tenant data
  tenant_id: tenantConfig.tenant_id,
  clinic_name: tenantConfig.clinic_name,
  clinic_phone: tenantConfig.clinic_phone,
  telegram_chat_id: tenantConfig.telegram_internal_chat_id,
  evolution_instance_name: tenantConfig.evolution_instance_name || extracted.instance_name,
  // Error data
  workflow_name: extracted.workflow?.name || 'Unknown',
  workflow_id: extracted.workflow?.id || 'Unknown',
  execution_id: extracted.execution?.id || 'Unknown',
  error_message: extracted.error?.message || 'Unknown error',
  error_node: extracted.error?.node?.name || 'Unknown',
  error_stack: extracted.error?.stack || 'No stack trace',
  timestamp: new Date().toISOString(),
  // Patient fallback data
  remote_jid: extracted.remote_jid,
  instance_name: tenantConfig.evolution_instance_name || extracted.instance_name
};

